import React from "react";
import axios from 'axios';

import { useNavigate, useParams } from "react-router-dom"

class ReservationMore extends React.Component
{
    constructor () {
        super();

        this.state = {
            tableData: [],
        };
    }

    componentDidMount() {
        if (this.first) return; this.first = true;

        let paymentid = this.props.params.paymentid;

        var token = sessionStorage.getItem('token');
        axios.get(`http://localhost:3025/` + paymentid + '/bookings', {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            this.setState(prevState => {
                let tableData = { ...prevState.tableData };
                tableData = response.data;                 
                return { tableData }; 
              });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    fetchViewCartHref(id){
        return 'view-cart/' + id;
    }

    render(){
        return(<div>
            <h2>MyReservations</h2>
            <hr />
            <table className="table table-striped">
                <thead className="thead-dark">
                    <tr>
                        <th>Bundle</th>
                        <th>Ticket count</th>
                        <th>Completed on</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.tableData.map(item => 
                    <tr key={item._id}>
                        <td>{item.bundle}</td>
                        <td>{item.ticketcount}</td>
                        <td>{item.created}</td>
                    </tr>
                    )}
                </tbody>
            </table>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();
    const params = useParams();

    return <ReservationMore {...props} navigation={navigation} params={params} />;
}
