import React from "react";
import axios from 'axios';

import { useNavigate, useParams } from "react-router-dom"

class Payment extends React.Component
{
    constructor () {
        super();

        var payment = sessionStorage.getItem('payment');

        this.state = {
            paymentoption: null,
            creditinfo: null,
            mobileinfo: null,
            payment: payment
        };
    }

    selectOption(option){
        this.setState(prevState => {
            let paymentoption = { ...prevState.paymentoption };
            paymentoption = option;                 
            return { paymentoption }; 
          });
    }

    handleCardInfo(e){
        this.setState(prevState => {
            let creditinfo = { ...prevState.creditinfo };
            creditinfo[e.target.name] = e.target.value;               
            return { creditinfo }; 
          });
    }

    handleMobileinfo(e){
        this.setState(prevState => {
            let mobileinfo = { ...prevState.mobileinfo };
            mobileinfo[e.target.name] = e.target.value;               
            return { mobileinfo }; 
          });
    }

    clearCart(){
        sessionStorage.removeItem('cart');
        sessionStorage.removeItem('payment');
    }

    processOrder(){
        let userid = sessionStorage.getItem('userid');

        let cart = sessionStorage.getItem('cart');
        cart = JSON.parse(cart);

        let bookings = [];
        cart.forEach(function(item){
            bookings.push({ticketbundleid: item.id, ticketcount: item.count, userid: userid});
        });

        console.log(cart);

        let order = {
            payment: {
                userid: userid,
                cardno: this.state.cardno !== null ? this.state.creditinfo?.cardno : '',
                mobileno: this.state.mobileinfo !== null ? this.state.mobileinfo.mobileno : '',
                amount: this.state.payment,
                status: 'completed'
            },
            bookings: bookings
        };
        
        var token = sessionStorage.getItem('token');
        axios.post(`http://localhost:3025/booking`, order, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
            if (response.data.status) {
                alert(response.data.message);
                this.clearCart();
                this.props.navigation("/reservations");
            }else{
                alert(response.data.message);
            }
        })
        .catch(error => {
            alert(error);
        });

        axios.get(`http://localhost:3020/user/` + userid, {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            let emailInfo = {
                to: response.data.username,
                payment: this.state.payment
            };

            axios.post(`http://localhost:3025/email/confirmation`, emailInfo, {
                headers: {
                    'Authorization' : token
                }
            })
            .then(response => {
                alert(response.data.message);
            })
            .catch(error => {
                alert(error);
            });

            let smsInfo = {
                to: response.data.telno,
                payment: this.state.payment
            };

            axios.post(`http://localhost:3025/sms/confirmation`, smsInfo, {
                headers: {
                    'Authorization' : token
                }
            })
            .then(response => {
                alert(response.data.message);
            })
            .catch(error => {
                alert(error);
            });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    postToPaymentGateway(){
        this.state.creditinfo.price = this.state.payment;
        console.log(this.state.creditinfo);

        var token = sessionStorage.getItem('token');
        axios.post(`http://localhost:3023/payment`, this.state.creditinfo, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
            if (response.data.status) {
                alert(response.data.message);
                this.processOrder();
            }else{
                alert(response.data.message);
            }
        })
        .catch(error => {
            alert(error);
        });
    }

    postToMobilePaymentGateway(){
        this.state.mobileinfo.price = this.state.payment;
        console.log(this.state.mobileinfo);
        var token = sessionStorage.getItem('token');
        console.log(this.newTheater);
        axios.post(`http://localhost:3024/payment`, this.state.mobileinfo, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
            if (response.data.status) {
                alert(response.data.message);
                this.processOrder();
            }else{
                alert(response.data.message);
            }
        })
        .catch(error => {
            alert(error);
        });
    }

    render() {
        return (<div>
            <h4>Pay your Order</h4>
            <hr />
            <div>
                <div className="form-check mb-4">
                    <input className="form-check-input" type="radio" name="payment-option" value="card" onClick={this.selectOption.bind(this, 'card')}/>
                    <label>
                        Pay with Credit card
                    </label>
                </div>
                <div className="form-check mb-4">
                    <input className="form-check-input" type="radio" name="payment-option" value="mobile" onClick={this.selectOption.bind(this, 'mobile')}/>
                    <label>
                    Pay with your mobile bill
                    </label>
                </div>
            </div>
            <div className="option-pane">
                {this.state.paymentoption !== null && this.state.paymentoption === 'card' &&
                    <div>
                        <h6>#. Enter card information</h6>
                        <div className="card-payment">
                            <div className="row">
                                <div className="col">
                                    <label>CARD NUMBER</label>
                                    <input type="text" className="form-control" name="cardno" placeholder="Valid Card Number" onChange={this.handleCardInfo.bind(this)} />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col col-xs-6">
                                    <label>EXPIRATION DATE</label>
                                    <input type="text" className="form-control" name="expiration" placeholder="MM/YY" onChange={this.handleCardInfo.bind(this)} />
                                </div>
                                <div className="col col-xs-6">
                                    <label>CVC</label>
                                    <input type="text" className="form-control" name="cvc" placeholder="CVC" onChange={this.handleCardInfo.bind(this)} />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col">
                                    <label>CARD OWNER</label>
                                    <input type="text" className="form-control" name="owner" placeholder="Card Owner Name" onChange={this.handleCardInfo.bind(this)} />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col">
                                    <span className="highlight-value-wpd">Payment: Rs {this.state.payment}.00</span>
                                    <input type="button" className="btn btn-primary float-right" value="CHECKOUT" onClick={this.postToPaymentGateway.bind(this)}/>
                                </div>
                            </div>
                        </div>
                    </div>
                }

                {this.state.paymentoption !== null && this.state.paymentoption === 'mobile' &&
                    <div>
                        <h6>#. Enter mobile information</h6>
                        <div className="card-payment">
                            <div className="row">
                                <div className="col">
                                    <label>MOBILE</label>
                                    <input type="text" className="form-control" name="mobileno" placeholder="Mobile Phone number" onChange={this.handleMobileinfo.bind(this)} />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col">
                                    <label>PIN</label>
                                    <input type="text" className="form-control" name="pin" placeholder="Six digit pin number" onChange={this.handleMobileinfo.bind(this)} />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col">
                                    <span className="highlight-value-wpd">Payment: Rs {this.state.payment}.00</span>
                                    <input type="button" className="btn btn-primary float-right" value="CHECKOUT" onClick={this.postToMobilePaymentGateway.bind(this)} />
                                </div>
                            </div>
                        </div>
                    </div>
                }
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();
    const params = useParams();

    return <Payment {...props} navigation={navigation} params={params} />;
}
