import React from "react";
import axios from 'axios';

import { useNavigate, useParams } from "react-router-dom"

class UpdateTheater extends React.Component
{
    constructor () {
        super();
        this.state = {
            theater: null
        };
    }

    componentDidMount() {
        if (this.first) return; this.first = true;
        let id = this.props.params.id;
        var token = sessionStorage.getItem('token');
        axios.get(`http://localhost:3021/theater/` + id, {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            this.setState(prevState => {
                let theater = { ...prevState.theater };
                theater = response.data;                 
                return { theater }; 
              });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    handleTheaterUpdateChange(e){
        this.setState(prevState => {
            let theater = { ...prevState.theater };
            theater[e.target.name] = e.target.value;               
            return { theater }; 
          });
    }

    saveTheater(){
        this.state.theater.id = this.props.params.id;
        delete this.state.theater._id;
        console.log(this.state.theater);
        var token = sessionStorage.getItem('token');
        axios.put(`http://localhost:3021/theater`, this.state.theater, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
          console.log(response);
          this.props.navigation("/theaters");
        })
        .catch(error => {
          console.log(error);
        });
    }

    render() {
        return (<div>
            <h4>Update Theater</h4>
            <hr />
            <div className="editable-form">
                <form>
                    <div className="form-group">
                        <label>Name:</label>
                        <input className="form-control" value={this.state.theater?.name} type="text" name="name" onChange={this.handleTheaterUpdateChange.bind(this)} /> 
                    </div>
                    <div className="form-group">
                        <label>Location:</label>
                        <input className="form-control" value={this.state.theater?.location} type="text" name="location" onChange={this.handleTheaterUpdateChange.bind(this)} />
                    </div>
                    <br />
                    <input type="button" className="btn btn-primary" name="saveTheater" onClick={this.saveTheater.bind(this)} value="Save" />
                    &nbsp;&nbsp;
                    <a className="btn btn-warning" href="/theaters">Back</a>
                </form>
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();
    const params = useParams();

    return <UpdateTheater {...props} navigation={navigation} params={params} />;
}
