import React from "react";
import axios from 'axios';

import { useNavigate } from "react-router-dom"

class CreateMovie extends React.Component
{
    constructor () {
        super();
        this.newMovie = {};
    }

    handleMovieCreationChange(e){
        this.newMovie[e.target.name] = e.target.value;
    }

    createMovie(){
        var token = sessionStorage.getItem('token');
        console.log(this.newMovie);
        axios.post(`http://localhost:3021/movie`, this.newMovie, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
          console.log(response);
          this.props.navigation("/movies");
        })
        .catch(error => {
          console.log(error);
        });
    }

    render() {
        return (<div>
            <h4>Create a New Movie</h4>
            <hr />
            <div className="editable-form">
                <form>
                    <div className="form-group">
                        <label>Name:</label>
                        <input className="form-control" type="text" name="name" onChange={this.handleMovieCreationChange.bind(this)} /> 
                    </div>
                    <div className="form-group">
                        <label>Cast:</label>
                        <textarea className="form-control" type="text" name="cast" onChange={this.handleMovieCreationChange.bind(this)} rows="3"/>
                    </div>
                    <div className="form-group">
                        <label>Description:</label>
                        <textarea className="form-control" type="text" name="description" onChange={this.handleMovieCreationChange.bind(this)} rows="3"/>
                    </div>
                    <br />
                    <input type="button" className="btn btn-primary" name="createMovie" onClick={this.createMovie.bind(this)} value="Create" />
                    &nbsp;&nbsp;
                    <a className="btn btn-warning" href="/movies">Back</a>
                </form>
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();

    return <CreateMovie {...props} navigation={navigation} />;
}
