import React from "react";
import axios from 'axios';

import { useNavigate, useParams } from "react-router-dom"

class DeleteTheater extends React.Component
{
    constructor () {
        super();
        this.state = {
            theater: null
        };
    }

    componentDidMount() {
        if (this.first) return; this.first = true;
        let id = this.props.params.id;
        var token = sessionStorage.getItem('token');
        axios.get(`http://localhost:3021/theater/` + id, {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            this.setState(prevState => {
                let theater = { ...prevState.theater };
                theater = response.data;                 
                return { theater }; 
              });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    deleteTheater() {
        var token = sessionStorage.getItem('token');
        axios.delete(`http://localhost:3021/theater/` + this.props.params.id, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
          console.log(response);
          this.props.navigation("/theaters");
        })
        .catch(error => {
          console.log(error);
        });
    }

    render() {
        return (<div>
            <h4>Delete Theater</h4>
            <hr />
            <div className="editable-form">
                <form>
                    <div className="form-group">
                        <label className="highlight-key">Name</label>
                        <span className="highlight-value">: {this.state.theater?.name}</span>
                    </div>
                    <div className="form-group">
                        <label className="highlight-key">Location</label>
                        <label className="highlight-value">: {this.state.theater?.location}</label>
                    </div>
                    <br />
                    <input type="button" className="btn btn-danger" name="deleteTheater" onClick={this.deleteTheater.bind(this)} value="Delete" />
                    &nbsp;&nbsp;
                    <a className="btn btn-warning" href="/theaters">Back</a>
                </form>
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();
    const params = useParams();

    return <DeleteTheater {...props} navigation={navigation} params={params} />;
}
