import { Outlet, Link } from "react-router-dom";

import React from "react";
import axios from 'axios';

class Layout extends React.Component
{

  constructor(){
    super();

    this.state = {
      items : 0,
      links: {
        home: true,
        login: false,
        register : false,
        logout: false,
        username: false,
        viewcart: false,
        users: false,
        movies: false,
        theaters: false,
        tickets: false,
        booking: false,
        reservations: false
      }
    }

    // register
    let userId = sessionStorage.getItem('userid');
    if (userId) {
      this.state.links.login = false;
      this.state.links.register = false;
      this.state.links.logout = true;
      this.state.links.username = true;
    }
    else{
      this.state.links.login = true;
      this.state.links.register = true;
      this.state.links.logout = false;
      this.state.links.username = false;
    }

    let role = sessionStorage.getItem('userrole');

    if (role) {
      if (role === 'customer') {
        this.state.links.booking = true;
        this.state.links.reservations = true;
        this.state.links.viewcart = true;
      } else if (role === 'movie-admin') {
        this.state.links.theaters = true;
        this.state.links.movies = true;
        this.state.links.tickets = true;
      } else if (role === 'system-admin') {
        this.state.links.users = true;
      }
    }
  }

  getItemCount(){
    let cart = sessionStorage.getItem('cart');
    let itemCount = 0;

    if (cart) {
      cart = JSON.parse(cart);
      cart.forEach(function(item){
        itemCount += 1;
      });
    }

    return itemCount;
  }

  getUserName(){
    let username = sessionStorage.getItem('username');
    if (username) {
      return username.split(0, 2);
    }else{
      return '';
    }
  }

  signOut(){
    sessionStorage.removeItem('userid');
    sessionStorage.removeItem('userrole');
    sessionStorage.removeItem('username');
    sessionStorage.removeItem('token');
    sessionStorage.removeItem('cart');
    window.location = "/login";
  }

  render(){
    return (
      <>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li>
                {this.state.links.register && <Link className="nav-item nav-link" to="/register">Register</Link>}
              </li>
              <li>
                {this.state.links.login && <Link className="nav-item nav-link" to="/login">Login</Link>}
              </li>

            </ul>
          </div>
          {this.state.links.viewcart && <Link className="btn btn-primary" to="shop-cart">Cart <span className="badge" id="cart-item-count">[{this.getItemCount()}]</span></Link>}
          &nbsp;&nbsp;
          {this.state.links.username && <span className="login-name">{this.getUserName()}</span>}
          {this.state.links.logout && <a className="nav-item nav-link" href="/login" onClick={this.signOut}>Sign Out</a>}
        </nav>
        <br />
        <Outlet />
        <br />
      </>
    );
  }
}

