import axios from 'axios';

function Login() {



  function signIn(){
    axios.post(`http://localhost:3020/user/signin`, userCred)
    .then(function (response) {
      console.log(response);
      if (response.data !== null && response.data.status){
        sessionStorage.setItem('token', response.data.token);
        sessionStorage.setItem('userid', response.data.id);
        sessionStorage.setItem('userrole', response.data.role);
        sessionStorage.setItem('username', userCred.username);

        let role = response.data.role;
        if (role === 'customer') {
            window.location = "/book-tickets";
        } else if (role === 'movie-admin') {
            window.location = "/movies";
        } else if (role === 'system-admin') {
            window.location = "/users";
        }else {
            window.location = "/home";
        }
      } else {
        alert(response.data.message);
      }
    })
    .catch(function (error) {
      console.log(error);
      alert(error);
    });
  }

  return (
    <div className="editable-form">
        <h4>Register Topic Name</h4>
        <hr />
        <div className="form-group">
            
            <label>Username:</label> 
            <input className='form-control' type="text" name="username"  /> 
        </div>
        <div className="form-group">
            <label>Password:</label>
            <input className='form-control' type="password" name="password"  />
        </div>
        <br />
        <input className="btn btn-primary" type="button" name="btnSignUp" onClick={signIn} value="Sign In" />
    </div>
  );
}

export default Login;
