import React from "react";
import axios from 'axios';

import { useNavigate } from "react-router-dom"

class CreateTheater extends React.Component
{
    constructor () {
        super();
        this.newTheater = {};
    }

    handleTheaterCreationChange(e){
        console.log(this.newTheater);
        this.newTheater[e.target.name] = e.target.value;
    }

    createTheater(){
        var token = sessionStorage.getItem('token');
        console.log(this.newTheater);
        axios.post(`http://localhost:3021/theater`, this.newTheater, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
          console.log(response);
          this.props.navigation("/theaters");
        })
        .catch(error => {
          console.log(error);
        });
    }

    render() {
        return (<div>
            <h4>Create a New Theater</h4>
            <hr />
            <div className="editable-form">
                <form>
                    <div className="form-group">
                        <label>Name:</label>
                        <input className="form-control" type="text" name="name" onChange={this.handleTheaterCreationChange.bind(this)} /> 
                    </div>
                    <div className="form-group">
                        <label>Location:</label>
                        <input className="form-control" type="text" name="location" onChange={this.handleTheaterCreationChange.bind(this)} />
                    </div>
                    <br />
                    <input type="button" className="btn btn-primary" name="createTheater" onClick={this.createTheater.bind(this)} value="Create" />
                    &nbsp;&nbsp;
                    <a className="btn btn-warning" href="/theaters">Back</a>
                </form>
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();

    return <CreateTheater {...props} navigation={navigation} />;
}
