import React from "react";
import axios from 'axios';

class Home extends React.Component
{
  constructor(){
    super();
    this.state = {
      userservice : 'inactive',
      moviemanagementservice: 'inactive',
      bookingservice : 'inactive',
      paymentgatewayservice : 'inactive',
      mobilepaymentgatewayservice : 'inactive',
      deliveryservice : 'inactive',
      emailservice : 'inactive',
      smsservice : 'inactive',
    }
  }

  componentDidMount() {
    if (this.first) return; this.first = true;
    axios.get(`http://localhost:3020/version`)
    .then(res => {
      this.setState(prevState => {
        let userservice = { ...prevState.userservice };
        userservice = 'active';               
        return { userservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3021/version`)
    .then(res => {
      this.setState(prevState => {
        let moviemanagementservice = { ...prevState.moviemanagementservice };
        moviemanagementservice = 'active';               
        return { moviemanagementservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3022/version`)
    .then(res => {
      this.setState(prevState => {
        let bookingservice = { ...prevState.bookingservice };
        bookingservice = 'active';               
        return { bookingservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3023/version`)
    .then(res => {
      this.setState(prevState => {
        let paymentgatewayservice = { ...prevState.paymentgatewayservice };
        paymentgatewayservice = 'active';               
        return { paymentgatewayservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3024/version`)
    .then(res => {
      this.setState(prevState => {
        let mobilepaymentgatewayservice = { ...prevState.mobilepaymentgatewayservice };
        mobilepaymentgatewayservice = 'active';               
        return { mobilepaymentgatewayservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3025/version`)
    .then(res => {
      this.setState(prevState => {
        let deliveryservice = { ...prevState.deliveryservice };
        deliveryservice = 'active';               
        return { deliveryservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3026/version`)
    .then(res => {
      this.setState(prevState => {
        let emailservice = { ...prevState.emailservice };
        emailservice = 'active';               
        return { emailservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });

    axios.get(`http://localhost:3027/version`)
    .then(res => {
      this.setState(prevState => {
        let smsservice = { ...prevState.smsservice };
        smsservice = 'active';               
        return { smsservice }; 
      });
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  render(){

  };
}

export default Home;
