import React from "react";
import axios from 'axios';

class Reservation extends React.Component
{
    constructor () {
        super();

        this.state = {
            tableData: [],
        };
    }

    componentDidMount() {
        if (this.first) return; this.first = true;

        let userid = sessionStorage.getItem('userid');

        var token = sessionStorage.getItem('token');
        axios.get(`http://localhost:3025/` + userid + '/reservations', {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            this.setState(prevState => {
                let tableData = { ...prevState.tableData };
                tableData = response.data;                 
                return { tableData }; 
              });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    fetchViewCartHref(id){
        return 'reservation/' + id;
    }

    render(){
        return(<div>
            <h2>My Reservations</h2>
            <hr />
            <table className="table table-striped">
                <thead className="thead-dark">
                    <tr>
                        <th>Credit card number</th>
                        <th>Mobile number</th>
                        <th>Amount</th>
                        <th>Completed on</th>
                        <th>Status</th>
                        <th className="command-col-half">#</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.tableData.map(item => 
                    <tr key={item._id}>
                        <td>{item.cardno !== '' ? item.cardno : 'n/a'}</td>
                        <td>{item.mobileno !== '' ? item.mobileno : 'n/a'}</td>
                        <td>{item.amount}</td>
                        <td>{item.created}</td>
                        <td>{item.status}</td>
                        <td>
                            <a className="btn btn-success" href={this.fetchViewCartHref(item._id)}>More..</a>
                        </td>
                    </tr>
                    )}
                </tbody>
            </table>
        </div>);
    }
}

export default Reservation;
