import React from "react";
import axios from 'axios';

import { useNavigate, useParams } from "react-router-dom"

class UpdateMovie extends React.Component
{
    constructor () {
        super();
        this.state = {
            movie: null
        };
    }

    componentDidMount() {
        if (this.first) return; this.first = true;
        let id = this.props.params.id;
        var token = sessionStorage.getItem('token');
        axios.get(`http://localhost:3021/movie/` + id, {
            headers: {
            'Authorization' : token
            }
        })
        .then(response => {
            this.setState(prevState => {
                let movie = { ...prevState.movie };
                movie = response.data;                 
                return { movie }; 
              });
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    handleMovieUpdateChange(e){
        this.setState(prevState => {
            let movie = { ...prevState.movie };
            movie[e.target.name] = e.target.value;               
            return { movie }; 
          });
    }

    saveMovie(){
        this.state.movie.id = this.props.params.id;
        delete this.state.movie._id;
        console.log(this.state.movie);
        var token = sessionStorage.getItem('token');
        axios.put(`http://localhost:3021/movie`, this.state.movie, {
          headers: {
            'Authorization' : token
          }
        })
        .then(response => {
          console.log(response);
          this.props.navigation("/movies");
        })
        .catch(error => {
          console.log(error);
        });
    }

    render() {
        return (<div>
            <h4>Update Movie</h4>
            <hr />
            <div className="editable-form">
                <form>
                    <div className="form-group">
                        <label>Name:</label>
                        <input className="form-control" value={this.state.movie?.name} type="text" name="name" onChange={this.handleMovieUpdateChange.bind(this)} /> 
                    </div>
                    <div className="form-group">
                        <label>Cast:</label>
                        <textarea className="form-control" value={this.state.movie?.cast} type="text" name="cast" onChange={this.handleMovieUpdateChange.bind(this)} rows="3" />
                    </div>
                    <div className="form-group">
                        <label>Description:</label>
                        <textarea className="form-control" value={this.state.movie?.description} type="text" name="description" onChange={this.handleMovieUpdateChange.bind(this)} rows="3" />
                    </div>
                    <br />
                    <input type="button" className="btn btn-primary" name="createMovie" onClick={this.saveMovie.bind(this)} value="Save" />
                    &nbsp;&nbsp;
                    <a className="btn btn-warning" href="/movies">Back</a>
                </form>
            </div>
        </div>);
    }
}

// Wrap and export
export default function(props) {
    const navigation = useNavigate();
    const params = useParams();

    return <UpdateMovie {...props} navigation={navigation} params={params} />;
}
